        db?.execSQL("INSERT INTO APPOINTMENTS(CAR_INFO_ID, CUSTOMER_ID, DATE, TIME, SERVICE_REASON) VALUES (2, 1,'25 Apr, 2021', '04:30 PM', 'Check up')")

        db?.execSQL("INSERT INTO SERVICES(SERVICE_NAME, SERVICE_PRICE) VALUES ('Oil', '150')")
